```javascript:/js/mega-handler.js
async function getMegaFileUrl(megaUrl) {
    try {
        const file = await mega.file(megaUrl);
        return await file.downloadBuffer();
    } catch (error) {
        console.error('Error loading Mega file:', error);
        return null;
    }
}

async function playMegaTrack(url) {
    const buffer = await getMegaFileUrl(url);
    if (buffer) {
        const blob = new Blob([buffer], { type: 'audio/mpeg' });
        const audioUrl = URL.createObjectURL(blob);
        player.src = audioUrl;
        player.play();
    }
}
```

