</head>
<body>
    <div class="player-container">
        <div class="player-controls">
            <button class="control-button" id="prevBtn"><i class="fas fa-backward"></i></button>
            <button class="control-button" id="playBtn"><i class="fas fa-play"></i></button>
            <button class="control-button" id="nextBtn"><i class="fas fa-forward"></i></button>
            <div class="progress-bar">
                <div class="progress" id="progress"></div>
            </div>
            <span id="currentTime">0:00</span> / <span id="duration">0:00</span>
        </div>

        <div class="playlist" id="playlist"></div>
    </div>

    <script>
        // Initialize cache
        const audioCache = new Map();

        class AudioPlayer {
            constructor() {
                this.playlist = [
                    { title: "Track 1", url: "https://mega.nz/file/clt00QqY#nP0Zk0wW4x6tSiLSisA20YjuDhTxBGwQZs52gcmEZNI" },
                    // ... rest of your tracks ...
                ];
                this.currentTrack = 0;
                this.isPlaying = false;
                this.audio = new Audio();
                this.initializePlayer();
            }

            async cacheTrack(url) {
                if (audioCache.has(url)) return audioCache.get(url);

                try {
                    const response = await fetch(url);
                    const blob = await response.blob();
                    const objectUrl = URL.createObjectURL(blob);
                    audioCache.set(url, objectUrl);
                    return objectUrl;
                } catch (error) {
                    console.error('Caching failed:', error);
                    return url;
                }
            }

            createTrackElement(track, index) {
                const div = document.createElement('div');
                div.className = 'track-item';
                div.innerHTML = `
                    <div class="track-info">
                        <div class="track-title">${track.title}</div>
                        <div class="track-duration" id="duration-${index}">--:--</div>
                    </div>
                    <div class="action-buttons">
                        <button class="btn btn-play" onclick="player.playTrack(${index})">
                            <i class="fas fa-play"></i> Play
                        </button>
                        <button class="btn btn-download" onclick="player.downloadTrack('${track.url}')">
                            <i class="fas fa-download"></i> Download
                        </button>
                    </div>
                    <div class="cached-indicator" id="cache-${index}" style="display: none">
                        <i class="fas fa-check-circle"></i> Cached
                    </div>
                `;
                return div;
            }

            initializePlayer() {
                const playlist = document.getElementById('playlist');
                this.playlist.forEach((track, index) => {
                    playlist.appendChild(this.createTrackElement(track, index));
                });

                // Add event listeners for controls
                document.getElementById('playBtn').onclick = () => this.togglePlay();
                document.getElementById('prevBtn').onclick = () => this.previousTrack();
                document.getElementById('nextBtn').onclick = () => this.nextTrack();

                // Progress bar updates
                this.audio.ontimeupdate = () => this.updateProgress();
            }

            async playTrack(index) {
                this.currentTrack = index;
                const track = this.playlist[index];

                // Check cache first
                if (audioCache.has(track.url)) {
                    this.audio.src = audioCache.get(track.url);
                } else {
                    await this.cacheTrack(track.url);
                    this.audio.src = audioCache.get(track.url);
                }

                this.audio.play();
                this.isPlaying = true;
                this.updatePlayButton();
                this.showCachedIndicator(index);
            }

            togglePlay() {
                if (this.isPlaying) {
                    this.audio.pause();
                } else {
                    this.audio.play();
                }
                this.isPlaying = !this.isPlaying;
                this.updatePlayButton();
            }

            updatePlayButton() {
                const playBtn = document.getElementById('playBtn');
                playBtn.innerHTML = this.isPlaying ? 
                    '<i class="fas fa-pause"></i>' : 
                    '<i class="fas fa-play"></i>';
            }

            updateProgress() {
                const progress = document.getElementById('progress');
                const currentTime = document.getElementById('currentTime');
                const duration = document.getElementById('duration');

                const percent = (this.audio.currentTime / this.audio.duration) * 100;
                progress.style.width = percent + '%';

                currentTime.textContent = this.formatTime(this.audio.currentTime);
                duration.textContent = this.formatTime(this.audio.duration);
            }

            formatTime(seconds) {
                const minutes = Math.floor(seconds / 60);
                seconds = Math.floor(seconds % 60);
                return `${minutes}:${seconds.toString().padStart(2, '0')}`;
            }

            showCachedIndicator(index) {
                const indicator = document.getElementById(`cache-${index}`);
                indicator.style.display = 'flex';
            }

            async downloadTrack(url) {
                window.open(url, '_blank');
            }

            nextTrack() {
                this.playTrack((this.currentTrack + 1) % this.playlist.length);
            }

            previousTrack() {
                this.playTrack(this.currentTrack === 0 ? 
                    this.playlist.length - 1 : 
                    this.currentTrack - 1);
            }
        }

        const player = new AudioPlayer();
    </script>
</body>
