```javascript
                { title: "Track 23", url: "https://mega.nz/file/pg0nEYSL#U4pq8ayxB8PS_l09S8dBUFjpMfb_9cLGMDLt-6Ki4Yw" },
                { title: "Track 24", url: "https://mega.nz/file/IplkWDiQ#L100tBQtnn4Y6lHV66EgvUIsWHvmOOzjJytl8iAhZ_s" }
            ];

            const playlistContainer = document.getElementById('playlist');
            let currentTrack = 0;
            let isPlaying = false;
            const audio = new Audio();

            // Create playlist items
            playlist.forEach((track, index) => {
                const trackElement = document.createElement('div');
                trackElement.className = 'track-item';
                trackElement.innerHTML = `
                    <div class="track-info">
                        <div class="track-title">${track.title}</div>
                    </div>
                    <div class="action-buttons">
                        <button class="btn btn-play" onclick="playTrack(${index})">
                            <i class="fas fa-play"></i> Play
                        </button>
                        <a href="${track.url}" class="btn btn-download" target="_blank">
                            <i class="fas fa-download"></i> Download
                        </a>
                    </div>
                `;
                playlistContainer.appendChild(trackElement);
            });

            // Player functions
            window.playTrack = function(index) {
                currentTrack = index;
                audio.src = playlist[index].url;
                audio.play();
                isPlaying = true;
                updatePlayButton();
                highlightCurrentTrack();
            };

            function updatePlayButton() {
                const playBtn = document.getElementById('playBtn');
                playBtn.innerHTML = isPlaying ? 
                    '<i class="fas fa-pause"></i>' : 
                    '<i class="fas fa-play"></i>';
            }

            function highlightCurrentTrack() {
                const tracks = document.querySelectorAll('.track-item');
                tracks.forEach((track, index) => {
                    track.style.backgroundColor = index === currentTrack ? '#f0f7ff' : '';
                });
            }

            // Control button event listeners
            document.getElementById('playBtn').addEventListener('click', () => {
                if (isPlaying) {
                    audio.pause();
                } else {
                    audio.play();
                }
                isPlaying = !isPlaying;
                updatePlayButton();
            });

            document.getElementById('nextBtn').addEventListener('click', () => {
                currentTrack = (currentTrack + 1) % playlist.length;
                playTrack(currentTrack);
            });

            document.getElementById('prevBtn').addEventListener('click', () => {
                currentTrack = (currentTrack - 1 + playlist.length) % playlist.length;
                playTrack(currentTrack);
            });

            // Progress bar updates
            audio.addEventListener('timeupdate', () => {
                const progress = document.getElementById('progress');
                const currentTime = document.getElementById('currentTime');
                const duration = document.getElementById('duration');

                const percent = (audio.currentTime / audio.duration) * 100;
                progress.style.width = percent + '%';

                currentTime.textContent = formatTime(audio.currentTime);
                duration.textContent = formatTime(audio.duration);
            });

            function formatTime(seconds) {
                const minutes = Math.floor(seconds / 60);
                seconds = Math.floor(seconds % 60);
                return `${minutes}:${seconds.toString().padStart(2, '0')}`;
            }
        });
    </script>
</body>
</html>
