(function () {
  var scriptURL = 'https://sdks.shopifycdn.com/buy-button/latest/buy-button-storefront.min.js';
  let page = 1;
  let loading = false;
  let preloadTriggered = false;
  const preloadThreshold = 2000; // Pixels before bottom to trigger preload

  function addImageCycling() {
    const products = document.querySelectorAll('.shopify-buy__product');

    products.forEach(product => {
      const images = product.querySelectorAll('.shopify-buy__product__variant-img');
      if (!images.length) return;

      let currentIndex = 0;
      let interval;

      const startInterval = () => {
        interval = setInterval(() => {
          images[currentIndex].style.opacity = '0';
          currentIndex = (currentIndex + 1) % images.length;
          images[currentIndex].style.opacity = '1';
        }, 4000);
      };

      const productWrapper = product.querySelector('.shopify-buy__product-img-wrapper');
      productWrapper.addEventListener('mouseenter', () => {
        clearInterval(interval);
      });

      productWrapper.addEventListener('mouseleave', () => {
        startInterval();
      });

      startInterval();
    });
  }

  function preloadNextPage() {
    if (loading || preloadTriggered) return;

    loading = true;
    preloadTriggered = true;
    document.querySelector('.loading-spinner').style.display = 'block';

    // Simulate API call for next page
    setTimeout(() => {
      loading = false;
      document.querySelector('.loading-spinner').style.display = 'none';
      page++;
      preloadTriggered = false;
      addImageCycling(); // Reinitialize cycling for new products
    }, 1000);
  }

  function checkScroll() {
    const scrollPosition = window.innerHeight + window.pageYOffset;
    const pageHeight = document.documentElement.scrollHeight;
    const distanceToBottom = pageHeight - scrollPosition;

    if (distanceToBottom < preloadThreshold) {
      preloadNextPage();
    }
  }

  if (window.ShopifyBuy) {
    if (window.ShopifyBuy.UI) {
      ShopifyBuyInit();
    } else {
      loadScript();
    }
  } else {
    loadScript();
  }

  function loadScript() {
    var script = document.createElement('script');
    script.async = true;
    script.src = scriptURL;
    (document.getElementsByTagName('head')[0] || document.getElementsByTagName('body')[0]).appendChild(script);
    script.onload = ShopifyBuyInit;
  }

  function ShopifyBuyInit() {
    var client = ShopifyBuy.buildClient({
      domain: '7y105d-qv.myshopify.com',
      storefrontAccessToken: 'ab4983432ad7af5334428f578412b8c5',
    });

    ShopifyBuy.UI.onReady(client).then(function (ui) {
      ui.createComponent('collection', {
        id: '345007423670',
        node: document.getElementById('collection-component-1735549545477'),
        moneyFormat: '%24%7B%7Bamount%7D%7D',
        options: {
          "product": {
            "styles": {
              "product": {
                "background-color": "var(--primary-color)",
                "color": "var(--text-color)",
                "@media (min-width: 601px)": {
                  "max-width": "100%",
                  "margin-left": "0",
                  "margin-bottom": "40px",
                  "width": "100%",
                  "transition": "all var(--transition-speed) ease"
                },
                ":hover": {
                  "transform": "scale(1.02)"
                }
              },
              "button": {
                "background-color": "var(--accent-color)",
                ":hover": {
                  "background-color": "#ff4d7d"
                }
              }
            },
            "text": {
              "button": "Add to cart"
            }
          },
          "productSet": {
            "styles": {
              "products": {
                "background-color": "var(--primary-color)"
              }
            }
          },
          "modalProduct": {
            "contents": {
              "img": false,
              "imgWithCarousel": true,
              "button": false,
              "buttonWithQuantity": true
            },
            "styles": {
              "product": {
                "background-color": "var(--primary-color)",
                "@media (min-width: 601px)": {
                  "max-width": "100%",
                  "margin-left": "0"
                }
              }
            }
          }
        }
      }).then(() => {
        setTimeout(addImageCycling, 1000);
        window.addEventListener('scroll', checkScroll, { passive: true });
        // Initial check in case page is short
        setTimeout(checkScroll, 1000);
      });
    });
  }
})();
