(function () {
  // [Previous script content remains...]

  // Add new filter functionality
  let productTypes = new Set();
  let currentFilters = {
    price: '',
    type: ''
  };

  function initializeFilters() {
    const products = document.querySelectorAll('.shopify-buy__product');
    products.forEach(product => {
      const type = product.getAttribute('data-product-type');
      if (type) productTypes.add(type);
    });

    const typeFilter = document.getElementById('type-filter');
    productTypes.forEach(type => {
      const option = document.createElement('option');
      option.value = type;
      option.textContent = type;
      typeFilter.appendChild(option);
    });
  }

  function applyFilters() {
    const products = document.querySelectorAll('.shopify-buy__product');
    products.forEach(product => {
      let show = true;

      if (currentFilters.type && product.getAttribute('data-product-type') !== currentFilters.type) {
        show = false;
      }

      if (currentFilters.price) {
        const price = parseFloat(product.getAttribute('data-product-price'));
        const [min, max] = currentFilters.price.split('-').map(Number);
        if (max && (price < min || price > max)) show = false;
        if (!max && price < min) show = false;
      }

      product.style.display = show ? 'block' : 'none';
    });
  }

  document.getElementById('price-filter').addEventListener('change', (e) => {
    currentFilters.price = e.target.value;
    applyFilters();
  });

  document.getElementById('type-filter').addEventListener('change', (e) => {
    currentFilters.type = e.target.value;
    applyFilters();
  });

  document.getElementById('back-to-top').addEventListener('click', () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  });

  // Enhance ShopifyBuyInit to include filter initialization
  const originalShopifyBuyInit = ShopifyBuyInit;
  ShopifyBuyInit = function() {
    originalShopifyBuyInit();
    setTimeout(() => {
      initializeFilters();
      addImageCycling();
    }, 1000);
  };

  // Show/hide back to top button based on scroll
  window.addEventListener('scroll', () => {
    const backToTop = document.getElementById('back-to-top');
    backToTop.style.opacity = window.scrollY > 300 ? '1' : '0';
  });

})();
/*]]>*/
