            box-shadow: 0 0 20px #0f0;
        }

        .iframe-container {
            width: 600px;
            height: 400px;
            overflow: hidden;
        }

        iframe {
            width: 100%;
            height: 100%;
            border: none;
        }

        .close-btn {
            position: absolute;
            right: 10px;
            top: 10px;
            color: #ff69b4;
            cursor: pointer;
            font-size: 24px;
        }
    </style>
</head>
<body>
    <div class="sms-container">
        <h1 class="neon-title">Retro SMS Sender</h1>
        <input type="tel" id="phone" placeholder="Enter Phone Number (with country code)">
        <textarea id="message" placeholder="Type your message here"></textarea>
        <button onclick="sendSMS()">Send Message 🚀</button>
    </div>

    <div id="smsModal" class="modal">
        <span class="close-btn" onclick="closeModal()">×</span>
        <div class="iframe-container">
            <iframe id="smsFrame"></iframe>
        </div>
    </div>

    <script>
        function sendSMS() {
            const phone = document.getElementById('phone').value;
            const message = document.getElementById('message').value;
            const url = `https://globfone.com/send-text/?phone=${encodeURIComponent(phone)}&message=${encodeURIComponent(message)}`;

            document.getElementById('smsFrame').src = url;
            document.getElementById('smsModal').style.display = 'block';
        }

        function closeModal() {
            document.getElementById('smsModal').style.display = 'none';
            document.getElementById('smsFrame').src = '';
        }

        // Close modal if clicking outside
        window.onclick = function(event) {
            const modal = document.getElementById('smsModal');
            if (event.target == modal) {
                closeModal();
            }
        }
    </script>
</body>
</html>
