```javascript:src/js/main.js
const gameData = {
    init() {
        this.db = new Dexie('GameDesignDB');
        this.db.version(1).stores({
            nodes: '++id, parent, type, content',
            assets: '++id, nodeId, type, data',
            code: '++id, nodeId, language, content'
        });
    },

    async addNode(data) {
        return await this.db.nodes.add({
            parent: data.parent,
            type: data.type,
            content: data.content
        });
    },

    async addAsset(file, nodeId) {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = async () => {
            await this.db.assets.add({
                nodeId: nodeId,
                type: file.type,
                data: reader.result
            });
        };
    },

    exportProject() {
        const zip = new JSZip();
        this.db.nodes.toArray().then(nodes => {
            zip.file('structure.json', JSON.stringify(nodes));
        });
        this.db.assets.toArray().then(assets => {
            assets.forEach(asset => {
                zip.file(`assets/${asset.id}`, asset.data);
            });
        });
        zip.generateAsync({type: 'blob'})
            .then(content => {
                saveAs(content, 'game-project.zip');
            });
    }
};
