Here's a self-contained, offline-capable game development organizer:

```html:src/game_organizer.html
<!DOCTYPE html>
<html>
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Game Development Organizer</title>
    <style>
        /* Embedding CSS directly for offline use */
        .node-tree {
            margin-left: 20px;
            border-left: 2px solid #ccc;
            padding-left: 10px;
        }

        .collapsible {
            background: #f1f1f1;
            padding: 10px;
            cursor: pointer;
            border-radius: 4px;
            margin: 5px 0;
        }

        .content {
            display: none;
            padding: 10px;
            background: #fff;
        }

        .sprite-viewer {
            max-width: 300px;
            max-height: 300px;
            border: 1px solid #ddd;
        }

        .tooltip {
            position: absolute;
            background: #333;
            color: #fff;
            padding: 5px;
            border-radius: 3px;
            display: none;
        }

        select, input, textarea {
            width: 100%;
            padding: 8px;
            margin: 5px 0;
        }

        .category-container {
            border: 1px solid #eee;
            margin: 10px 0;
            padding: 10px;
        }

        .expanded {
            display: block;
        }

        .spinner {
            width: 50px;
            height: 50px;
            border: 3px solid #f3f3f3;
            border-top: 3px solid #3498db;
            border-radius: 50%;
            animation: spin 1s linear infinite;
            display: none;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }
    </style>
</head>
<body>
    <div id="mainContainer">
        <h1>Game Development Organizer</h1>

        <div class="category-container">
            <div class="collapsible">Timeline Management ▼</div>
            <div class="content">
                <select id="timelineSelect">
                    <option value="timelineA">Timeline A - Success</option>
                    <option value="timelineB">Timeline B - Failure</option>
                </select>
                <input type="text" placeholder="Add new event" data-tooltip="Enter timeline event details">
                <textarea placeholder="Event description" data-tooltip="Detailed description of the event"></textarea>
            </div>
        </div>

        <div class="category-container">
            <div class="collapsible">Character Management ▼</div>
            <div class="content">
                <select id="characterType">
                    <option value="player">Player Character</option>
                    <option value="npc">NPC</option>
                    <option value="enemy">Enemy</option>
                </select>
                <input type="text" placeholder="Character name" data-tooltip="Enter character name">
                <textarea placeholder="Character traits" data-tooltip="List character attributes"></textarea>
                <div class="sprite-viewer">
                    <img id="characterSprite" src="" alt="Character Sprite">
                </div>
                <input type="file" accept="image/*" data-tooltip="Upload character sprite">
            </div>
        </div>

        <div class="category-container">
            <div class="collapsible">Location Editor ▼</div>
            <div class="content">
                <select id="locationSelect">
                    <option value="street">Street Level</option>
                    <option value="hostel">Hostel</option>
                    <option value="restaurant">Restaurant</option>
                </select>
                <textarea placeholder="Location description" data-tooltip="Describe the location"></textarea>
                <input type="file" multiple accept="image/*" data-tooltip="Upload location assets">
            </div>
        </div>

        <div class="category-container">
            <div class="collapsible">Item Database ▼</div>
            <div class="content">
                <input type="text" placeholder="Item name" data-tooltip="Enter item name">
                <select id="itemType">
                    <option value="weapon">Weapon</option>
                    <option value="key">Key Item</option>
                    <option value="evidence">Evidence</option>
                </select>
                <textarea placeholder="Item properties" data-tooltip="List item attributes"></textarea>
            </div>
        </div>
    </div>

    <button onclick="exportProject()">Export Project</button>
    <div id="spinner" class="spinner"></div>

    <script>
        // Embedded JavaScript for offline functionality
        document.addEventListener('DOMContentLoaded', function() {
            // Initialize local storage
            let gameData = localStorage.getItem('gameData') ? 
                JSON.parse(localStorage.getItem('gameData')) : 
                {
                    timelines: {},
                    characters: {},
                    locations: {},
                    items: {}
                };

            // Collapsible functionality
            document.querySelectorAll('.collapsible').forEach(item => {
                item.addEventListener('click', function() {
                    this.classList.toggle('active');
                    let content = this.nextElementSibling;
                    content.style.display = content.style.display === 'block' ? 'none' : 'block';
                });
            });

            // Tooltip functionality
            document.querySelectorAll('[data-tooltip]').forEach(element => {
                element.addEventListener('mouseover', function(e) {
                    let tooltip = document.createElement('div');
                    tooltip.className = 'tooltip';
                    tooltip.textContent = this.dataset.tooltip;
                    document.body.appendChild(tooltip);
                    tooltip.style.top = e.pageY + 10 + 'px';
                    tooltip.style.left = e.pageX + 10 + 'px';
                    tooltip.style.display = 'block';
                });

                element.addEventListener('mouseout', function() {
                    document.querySelectorAll('.tooltip').forEach(t => t.remove());
                });
            });

            // File handling
            document.querySelectorAll('input[type="file"]').forEach(input => {
                input.addEventListener('change', function(e) {
                    let file = e.target.files[0];
                    if (file) {
                        let reader = new FileReader();
                        reader.onload = function(e) {
                            let imgPreview = input.parentElement.querySelector('img');
                            if (imgPreview) {
                                imgPreview.src = e.target.result;
                            }
                        };
                        reader.readAsDataURL(file);
                    }
                });
            });

            // Auto-save functionality
            document.querySelectorAll('input, textarea, select').forEach(element => {
                element.addEventListener('change', function() {
                    saveData();
                });
            });

            function saveData() {
                localStorage.setItem('gameData', JSON.stringify(gameData));
            }

            function exportProject() {
                let dataStr = "data:text/json;charset=utf-8," + 
                    encodeURIComponent(JSON.stringify(gameData));
                let downloadAnchorNode = document.createElement('a');
                downloadAnchorNode.setAttribute("href", dataStr);
                downloadAnchorNode.setAttribute("download", "game_project.json");
                document.body.appendChild(downloadAnchorNode);
                downloadAnchorNode.click();
                downloadAnchorNode.remove();
            }
        });
