```javascript:js/node-manager.js
class NodeManager {
    constructor() {
        this.nodes = new Map();
        this.activeNode = null;
    }

    createNode(type, data) {
        const node = {
            id: `node_${Date.now()}`,
            type: type,
            data: data,
            children: [],
            parent: null,
            position: { x: 0, y: 0 },
            created: new Date(),
            modified: new Date()
        };

        this.nodes.set(node.id, node);
        this.updateDatabase(node);
        return node;
    }

    updateNode(nodeId, newData) {
        const node = this.nodes.get(nodeId);
        if (node) {
            node.data = { ...node.data, ...newData };
            node.modified = new Date();
            this.updateDatabase(node);
            this.refreshNodeView(node);
        }
        return node;
    }

    renameNode(nodeId, newName) {
        const node = this.nodes.get(nodeId);
        if (node) {
            node.data.name = newName;
            node.modified = new Date();
            this.updateDatabase(node);
            this.refreshNodeView(node);
        }
        return node;
    }

    deleteNode(nodeId) {
        const node = this.nodes.get(nodeId);
        if (node) {
            // Remove child references
            if (node.parent) {
                const parent = this.nodes.get(node.parent);
                parent.children = parent.children.filter(id => id !== nodeId);
            }
            // Delete children recursively
            node.children.forEach(childId => this.deleteNode(childId));
            // Remove from database
            this.removeFromDatabase(nodeId);
            // Remove from memory
            this.nodes.delete(nodeId);
        }
    }

    addChild(parentId, childId) {
        const parent = this.nodes.get(parentId);
        const child = this.nodes.get(childId);
        if (parent && child) {
            parent.children.push(childId);
            child.parent = parentId;
            this.updateDatabase(parent);
            this.updateDatabase(child);
        }
    }

    moveNode(nodeId, newX, newY) {
        const node = this.nodes.get(nodeId);
        if (node) {
            node.position = { x: newX, y: newY };
            this.updateDatabase(node);
            this.refreshNodeView(node);
        }
    }

    duplicateNode(nodeId) {
        const original = this.nodes.get(nodeId);
        if (original) {
            const clone = {
                ...original,
                id: `node_${Date.now()}`,
                children: [],
                parent: null,
                created: new Date(),
                modified: new Date()
            };
            this.nodes.set(clone.id, clone);
            this.updateDatabase(clone);
            return clone;
        }
    }

    async updateDatabase(node) {
        await gameDB.saveToStore('nodes', node);
        this.emit('nodeUpdated', node);
    }

    async removeFromDatabase(nodeId) {
        await gameDB.deleteFromStore('nodes', nodeId);
        this.emit('nodeDeleted', nodeId);
    }

    refreshNodeView(node) {
        const element = document.querySelector(`[data-node-id="${node.id}"]`);
        if (element) {
            element.querySelector('.node-title').textContent = node.data.name;
            element.style.transform = `translate(${node.position.x}px, ${node.position.y}px)`;
        }
    }

    emit(eventName, data) {
        const event = new CustomEvent(eventName, { detail: data });
        document.dispatchEvent(event);
    }
}

