class NodeRenderer {
    constructor(container, nodeManager) {
        this.container = container;
        this.nodeManager = nodeManager;
        this.initializeCanvas();
        this.bindEvents();
    }

    renderNode(node) {
        const template = NodeTemplates[node.type];
        const element = document.createElement('div');
        element.className = 'node';
        element.dataset.nodeId = node.id;
        element.style.backgroundColor = template.color;

        element.innerHTML = `
            <div class="node-header">
                <span class="node-title">${node.data.name}</span>
                <div class="node-controls">
                    <button class="edit-node">Edit</button>
                    <button class="delete-node">Delete</button>
                </div>
            </div>
            <div class="node-content">
                ${this.renderFields(node, template.fields)}
            </div>
            <div class="node-ports">
                <div class="port input"></div>
                <div class="port output"></div>
            </div>
        `;

        this.container.appendChild(element);
        this.makeNodeDraggable(element);
    }

    renderFields(node, fields) {
        return fields.map(field => {
            return this.renderField(field, node.data[field.name]);
        }).join('');
    }

    renderField(field, value) {
        switch(field.type) {
            case 'text':
                return `<input type="text" value="${value || ''}" data-field="${field.name}">`;
            case 'textarea':
                return `<textarea data-field="${field.name}">${value || ''}</textarea>`;
            case 'select':
                return `
                    <select data-field="${field.name}">
                        ${field.options.map(opt => `
                            <option value="${opt}" ${value === opt ? 'selected' : ''}>
                                ${opt}
                            </option>
                        `).join('')}
                    </select>
                `;
            default:
                return '';
        }
    }

    makeNodeDraggable(element) {
        let pos = { x: 0, y: 0 };

        element.querySelector('.node-header').onmousedown = dragMouseDown;

        function dragMouseDown(e) {
            e.preventDefault();
            pos.x = e.clientX;
            pos.y = e.clientY;
            document.onmouseup = closeDragElement;
            document.onmousemove = elementDrag;
        }

        function elementDrag(e) {
            e.preventDefault();
            const dx = e.clientX - pos.x;
            const dy = e.clientY - pos.y;
            pos.x = e.clientX;
            pos.y = e.clientY;
            element.style.top = `${element.offsetTop + dy}px`;
            element.style.left = `${element.offsetLeft + dx}px`;
        }

        function closeDragElement() {
            document.onmouseup = null;
            document.onmousemove = null;
        }
    }
}
