```javascript:js/connection-manager.js
class ConnectionManager {
    constructor(container) {
        this.container = container;
        this.connections = new Map();
        this.activePath = null;
        this.svg = this.createSVGLayer();
        this.initializeEvents();
    }

    createSVGLayer() {
        const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
        svg.style.position = 'absolute';
        svg.style.width = '100%';
        svg.style.height = '100%';
        svg.style.pointerEvents = 'none';
        this.container.appendChild(svg);
        return svg;
    }

    createConnection(startPort, endPort) {
        const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        path.setAttribute('stroke', '#3498db');
        path.setAttribute('stroke-width', '2');
        path.setAttribute('fill', 'none');
        this.svg.appendChild(path);

        const connection = {
            id: `conn_${Date.now()}`,
            start: startPort,
            end: endPort,
            path: path
        };

        this.connections.set(connection.id, connection);
        this.updateConnection(connection);
        return connection;
    }

    updateConnection(connection) {
        const start = this.getPortPosition(connection.start);
        const end = this.getPortPosition(connection.end);
        const curve = this.calculateCurve(start, end);
        connection.path.setAttribute('d', curve);
    }

    calculateCurve(start, end) {
        const dx = end.x - start.x;
        const dy = end.y - start.y;
        const ctrl1 = { x: start.x + dx * 0.5, y: start.y };
        const ctrl2 = { x: end.x - dx * 0.5, y: end.y };
        return `M ${start.x},${start.y} C ${ctrl1.x},${ctrl1.y} ${ctrl2.x},${ctrl2.y} ${end.x},${end.y}`;
    }

    getPortPosition(port) {
        const rect = port.getBoundingClientRect();
        const container = this.container.getBoundingClientRect();
        return {
            x: rect.left + rect.width / 2 - container.left,
            y: rect.top + rect.height / 2 - container.top
        };
    }
}
