```javascript:js/history-manager.js
class HistoryManager {
    constructor(nodeManager) {
        this.nodeManager = nodeManager;
        this.history = [];
        this.currentIndex = -1;
        this.maxHistory = 50;
    }

    pushState(action, data) {
        const state = {
            action: action,
            data: this.cloneData(data),
            timestamp: Date.now()
        };

        // Remove future states if we're not at the end
        if (this.currentIndex < this.history.length - 1) {
            this.history = this.history.slice(0, this.currentIndex + 1);
        }

        // Add new state
        this.history.push(state);
        this.currentIndex++;

        // Limit history size
        if (this.history.length > this.maxHistory) {
            this.history.shift();
            this.currentIndex--;
        }
    }

    undo() {
        if (this.currentIndex > 0) {
            this.currentIndex--;
            const state = this.history[this.currentIndex];
            this.applyState(state);
        }
    }

    redo() {
        if (this.currentIndex < this.history.length - 1) {
            this.currentIndex++;
            const state = this.history[this.currentIndex];
            this.applyState(state);
        }
    }

    applyState(state) {
        switch (state.action) {
            case 'createNode':
                this.nodeManager.createNode(state.data.type, state.data.data);
                break;
            case 'updateNode':
                this.nodeManager.updateNode(state.data.id, state.data.newData);
                break;
            case 'deleteNode':
                this.nodeManager.deleteNode(state.data.id);
                break;
            case 'moveNode':
                this.nodeManager.moveNode(state.data.id, state.data.x, state.data.y);
                break;
        }
    }

    cloneData(data) {
        return JSON.parse(JSON.stringify(data));
    }
}

// Initialize both systems
