document.addEventListener('keydown', (e) => {
    if (e.ctrlKey || e.metaKey) {
        if (e.key === 'z') {
            e.preventDefault();
            historyManager.undo();
        }
        if (e.key === 'y' || (e.shiftKey && e.key === 'z')) {
            e.preventDefault();
            historyManager.redo();
        }
    }
});
