```javascript:js/audio-manager.js
class AudioManager {
    constructor() {
        this.audioNodes = new Map();
        this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
        this.analyser = this.audioContext.createAnalyser();
        this.initializeWaveformDisplay();
    }

    createAudioNode(file) {
        const reader = new FileReader();
        reader.onload = async (e) => {
            const audioBuffer = await this.audioContext.decodeAudioData(e.target.result);
            const node = {
                id: `audio_${Date.now()}`,
                buffer: audioBuffer,
                name: file.name,
                type: file.type,
                waveform: this.generateWaveformData(audioBuffer)
            };
            this.audioNodes.set(node.id, node);
            this.renderAudioNode(node);
        };
        reader.readAsArrayBuffer(file);
    }

    renderAudioNode(node) {
        const template = `
            <div class="audio-node" data-node-id="${node.id}">
                <div class="audio-controls">
                    <button class="play-btn">Play</button>
                    <button class="loop-btn">Loop</button>
                    <input type="range" class="volume" min="0" max="1" step="0.1" value="1">
                </div>
                <canvas class="waveform" width="200" height="60"></canvas>
                <div class="audio-info">
                    <span>${node.name}</span>
                    <select class="audio-type">
                        <option value="bgm">Background Music</option>
                        <option value="sfx">Sound Effect</option>
                        <option value="voice">Voice</option>
                    </select>
                </div>
            </div>
        `;

        const container = document.getElementById('audioContainer');
        container.insertAdjacentHTML('beforeend', template);
        this.drawWaveform(node);
    }

    drawWaveform(node) {
        const canvas = document.querySelector(`[data-node-id="${node.id}"] canvas`);
        const ctx = canvas.getContext('2d');
        const data = node.waveform;

        ctx.fillStyle = '#3498db';
        data.forEach((value, i) => {
            const x = (i / data.length) * canvas.width;
            const height = value * canvas.height;
            ctx.fillRect(x, canvas.height/2 - height/2, 2, height);
        });
    }

    generateWaveformData(audioBuffer) {
        const rawData = audioBuffer.getChannelData(0);
        const samples = 100;
        const blockSize = Math.floor(rawData.length / samples);
        const filteredData = [];

        for (let i = 0; i < samples; i++) {
            const blockStart = blockSize * i;
            let sum = 0;
            for (let j = 0; j < blockSize; j++) {
                sum += Math.abs(rawData[blockStart + j]);
            }
            filteredData.push(sum / blockSize);
        }

        return filteredData;
    }
}

